import React from "react";
import { IoMusicalNotesSharp, IoInformationCircleSharp } from "react-icons/io5";
import { Button } from "..";

import * as Styled from "./index.styled";

interface Props {
  onClose: () => void;
}

export function InfoPopUp({ onClose }: Props) {
  return (
    <Styled.Container>
      <Styled.PopUp>
        <h1>Welcome! ðŸ¤˜</h1>
        <Styled.Spacer />
        <Styled.Section>
          <IoMusicalNotesSharp size={60} />
          <p>
            Based on the popular game heardle but with Rock / Metal music{" "}
            <a href="https://heardle.app">Heardle</a>.
          </p>
        </Styled.Section>
        <Styled.Section>
          <IoInformationCircleSharp size={120} />
          <p>
            Listen to the intro, then find the correct artist and title in the
            list. Skipped or incorrect attempts unlock more of the intro. Answer
            in as few tries as possible and share your score!
          </p>
        </Styled.Section>
        <Button variant="green" style={{ marginTop: 20 }} onClick={onClose}>
          Close
        </Button>
        <Styled.Contact>
          AMC89 - <a href="rockheardle@gmail.com">rockheardle@gmail.com</a>{" "}
          <p>
            Thanks to{" "}
            <a href="https://twitter.com/synowski_maciej">Maciej Synowski</a>
          </p>
        </Styled.Contact>
      </Styled.PopUp>
    </Styled.Container>
  );
}
